pragma solidity 0.5.0;

contract TheBESTElection{
	//string public candidate;

	struct Player{
		uint id;
		string name;
		uint voteCount;
	}
	mapping(uint => Player) public players;
	//In this case, the key to the mapping is an unsigned integer,
	// and the value is a Candidate structure type that we just defined.
	mapping(address => bool) public voters;

	uint public playerCount;


	function addPlayer (string memory _name) private {
		playerCount++;
		players[playerCount]=Player(playerCount, _name, 0);
	}

	event votedEvent( uint indexed _playerId);
	//checks if any event comes with logs of given candId
	//above line added to check for trigger after voting

	function vote(uint _playerId) public{
		require(!voters[msg.sender]); 
		//did not vote before, ie voter[id] should be 0
		require(_playerId > 0 && _playerId <= playerCount);

		voters[msg.sender]=true; //here we vote and v[id] = 1

		players[_playerId].voteCount ++;

		//inc vote count of that cand by 1

		emit votedEvent(_playerId);

		//added to call it only after voting is called
		//added emit requres for new versions
	}

	constructor() public{ //updated constructor function
		addPlayer("Lionel Messi");
		addPlayer("Cristiano Ronaldo");
		addPlayer("Luka Modrić");

	}
}
